mapper.select:
SELECT customer_id customerId , store_id storeId , first_name firstName , last_name lastName , address_id addressId , email , create_date createDate , last_update lastUpdate FROM customer
first_name LIKE CONCAT('%', #{searchWord}, '%') OR last_name LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY customer_id DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO customer (
	store_id, first_name, last_name, email, address_id
) VALUES (
	#{storeId}, #{firstName}, #{lastName}, #{email}, #{addressId}
)
